import React from 'react'

function Pagination({page,totalPages,changePage}) {

    const changePageFunc = (value) => {
        if(value > 0 && value <= totalPages ){
            changePage(value)
        }else{
            console.log("sayfa yok")
        }
    }

  return (
    <div className="pagination">
        <div className="pagination-buttons">
            <button onClick = {changePageFunc.bind(this,page-1)} type="button">geri</button>
            <span>{page} -- {totalPages}</span>
            <button onClick = {changePageFunc.bind(this,page+1)} type="button">ileri</button>
        </div>
    </div>
  )
}
export default Pagination;
