import Header from './components/Header';
import axios from 'axios';
import './index.css';

function App() {

  const searchImages = async (getValue) => {
    const response = await axios.get('https://api.unsplash.com/search/photos',{
      headers: {
        Authorization: 'Client-ID Zr4Ol8ybEvmobviuv3V0_E8IaHBCWmWSQDSYPJ93Ymw'
      },
      params:{
        query:{
          getValue
        }
      }
    })

    return response
  }

  const handleSubmit = (getValue) => {
    console.log(getValue)
    searchImages(getValue);
  }
  return (
    <div className="app-container">
      <Header search = {handleSubmit} />
    </div>
  );
}

export default App;
