import Header from "./components/Header";
import searchImages from "./Api";
import "./index.css";
import { useState } from "react";
import ImageList from "./components/ImageList";
import Pagination from "./components/Pagination";

function App() {
  const [valueInput, setValue] = useState("");
  const [images, setImages] = useState([]);
  const [totalPage, setTotalPage] = useState(1);
  const [page, setPage] = useState(1);

  const handleChange = (e) => {
    e.preventDefault();
    setValue(e.target.value);
    handleSubmit(valueInput)
  };

  const handleSubmit = async (getValue, page) => {
    const result = await searchImages(getValue, page);
    setImages(result.results);
    setTotalPage(result.total_pages);
  };

  const changePageValue = (value) => {
    setPage(value);
    handleSubmit("car", value);
  };
  return (
    <div className="app-container">
      <Header handleChange = {handleChange} valueInput = {valueInput} search={handleSubmit} />
      <ImageList images={images} />
      <Pagination
        page={page}
        totalPages={totalPage}
        changePage={changePageValue}
      />
    </div>
  );
}

export default App;
